import React,{useState} from 'react'
import send from'../assets/img/Send.png'
import '../assets/css/Contact.css'
import {toast} from 'react-toastify'
import { Axios } from "axios";
export const Contact = () => {
    toast.configure()

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
 
 
  const [phone,setPhone ] =useState("");
  const [subject,setSubject]=useState("")
  const [detail, setDetail] = useState("");
  
  
  const handleSubmit=()=>{
    
    Axios.post('http://localhost:5000/Contact',
    {name:name,email:email,phone:phone,subject:subject,detail:detail})
    .then(()=>{
      alert("Contact data has been saved Successfully");
    }).catch(()=>{
     alert(" Contact data has not been saved Successfully ")
    })
  }

    return (
        <>
          <div className="Project-intro">
                <h3 className="Project-h3">
                   Get in touch
                </h3>
                <p className="Project-p">
                    Contact Me
                </p>
                <div className="Form-Control" id='Contact'>
                    <form  className="Form">
                        <p  className="Form-para">    Name</p>
                        <input type="text" id=""

                        value={name} onChange={(e) => setName(e.target.value)}

     className="Form-Input  Form-Dark-Input" placeholder=' Enter Your Name' />
                        
                        
<p  className="Form-para">   E-mail</p>

 <input type="email" id="" className="Form-Input Form-Dark-Input" 

 value={email} onChange={(e) => setEmail(e.target.value)}

 placeholder=' Enter Your E-mail'/>
 <p className="Form-para">   Phone Number</p>
  <input type="tel" id="" className="Form-Input Form-Dark-Input"

   value={phone} onChange={(e) => setPhone(e.target.value)}

  placeholder='Enter Your Phone Number' />    
  <p className="Form-para">Subject</p>
<input type="text"  id="" className="Form-Input Form-Dark-Input"

 value={subject} onChange={(e) => setSubject(e.target.value)}

placeholder='Enter Your Subject'/>
 <label htmlFor="detail" className="Form-para"> Express Your Subject in Details</label>
 <textarea id="" cols="30" rows="10" 

 onChange={(e) => setDetail(e.target.value)}

  className="Form-Input Form-Dark-Input " placeholder='Explain Your Subject in Details'></textarea>
  
 <button className="Form-Submit"type="submit" onClick={handleSubmit}><img src={send} /> Submit</button>
                    </form>
                </div>

            </div>   
        </>
    )
}
